INSERT INTO `tour` (`id_tour`, `name_tour`, `start`, `num_days`, `description`, `image`, `price`, `quantity_tickets`, `vehicle_id`) VALUES
(20, 'Dalat Wonder Resort Và Bữa Sáng, Xe Đưa Đón', '2021-07-23', 2, 'Một kỳ nghỉ dưỡng đặc biệt tại thành phố Đà Lạt mộng mơ cùng người thân và bạn bè với những trải nghiệm hấp dẫn tại Dalat Wonder Resort. Để tận hưởng trọn vẹn chuyến du lịch Đà lạt, hãy đặt ngay các g', 'assets/img/da-lat-wonder.jpg', 1350000, 10, 5),
(21, 'Kim Ngân Hill - Ga Đà Lạt - Vườn Hoa Thành Phố - Nhà Thờ Domain', '2021-07-23', 1, 'Hàng khách sẽ được tham quan Kim Ngân Hills: nổi bật với vườn thú kết hợp với không gian cà phê view rừng thông ấn tượng, xen kẻ là vườn hoa. Tiếp đó là Vườn Dâu Tây, Vườn Bí Ngô Khổng Lồ, Vườn Cà Chua Đen…: sống ảo với những nông sản đặc trưng của Đà Lạt (theo mùa). Sau đó, hành khách đến Nhà Ga Cổ Đà Lạt: nhà ga cổ nhất ở Việt Nam và đã được công nhận là di tích kiến trúc cấp quốc gia. Tiếp theo là Vườn Hoa Thành Phố: nơi đây là một trong những vườn hoa lớn nhất Việt Nam sở hữu không gian cực kỳ rộng lớn với tổng diện tích trồng hoa 7000m2. Và cuối cùng là Nhà Thờ Domain: nhà thờ “màu hồng” siêu xinh xắn, điểm sống ảo lý tưởng không thể bỏ qua.', 'assets/img/ga-da-lat.jpg', 450000, 10, 5),
(22, 'DU LỊCH PHÚ QUỐC - DU NGOẠN 3 ĐẢO ', '2021-07-14', 3, 'Tham quan Di tích lịch sử Nhà tù Phú Quốc. Viếng Dinh Bà Thủy Long Thánh Mẫu. Thờ Thần Nữ Kim Giao, người phụ nữ được người dân Phú Quốc rất mực tôn kính. Chiêm ngưỡng Hòn Móng Tay: Một trong những điểm check in tuyệt đẹp, tự do chụp hình, tắm biển.Hòn Ghầm Ghì: Nơi được biết là hòn đảo có rạn san hô đẹp nhất của quần đảo An Thới, thỏa sức khám phá thế giới đại dương kỳ thú. Hòn Mây Rút: Một hòn đảo tuyệt đẹp với bãi cát trắng trải dài, tự do tắm biển và đắm mình cùng thiên nhiênIn chương trình tour \r\n', 'assets/img/phuquoc.jpg', 4799000, 10, 5),
(23, 'DU LỊCH NHA TRANG', '2021-07-15', 3, 'Tham quan thành phố biển Nha Trang xinh đẹp.Tới khu du lịch Hòn Lao - đảo Khỉ, thư giãn, xem xiếc hoặc thử các môn thể thao bãi biển.', 'assets/img/nhatrang.jpg', 4600000, 10, 6),
(24, 'DU LỊCH HÀ NỘI', '2021-07-16', 5, 'Tham quan làng gốm Bát Tràng, dạo bộ ngắm hồ Hoàn Kiếm, “36 phố phường”.', 'assets/img/hanoi.jpg', 7579000, 10, 7),
(25, 'LÀO CAI - SA PA', '2021-07-17', 5, 'Ngắm toàn cảnh Sapa và “Nóc nhà Đông Dương” - đỉnh Fansipan tại sân mây ', 'assets/img/laocai.jpg', 7600000, 10, 7),
(26, 'Du lịch Đà Nẵng - Ngũ Hành Sơn - Bà Nà - Phố Cổ Hội An', '2021-07-18', 3, 'Du lịch Đà Nẵng hành trình khám phá thành phố biển sầm uất nhất dải đất Miền Trung nắng gió. Tham quan Ngũ Hành Sơn phong cảnh hữu tình với hệ thống các hang động, chùa chiền linh thiêng cổ kính. Một ngày vi vu trên cao nguyên Bà Nà để khám phá những tòa lâu đài mang đường lối kiến trúc Châu Âu. Xuôi về Quảng Nam, du khách sẽ phải ngỡ ngàng trước một Hội An yên bình nằm tọa lạc bên sông Thu Bồn. Phố Cổ Hội An hấp dẫn du khách bởi những dãy nhà màu vàng, mái ngói nằm san sát nhau, những cửa hàng truyền thống và ẩm thực phong phú. Đặc biệt trong tour du lịch Đà Nẵng này, du khách còn được trực tiếp xem show diễn Ký Ức Hội An. Đây là show diễn duy nhất của Việt Nam được công chiếu tại Quảng Trường Thời Đại - New York. Và được báo chí quốc tế ghi nhận là show diễn thực cảnh đẹp nhất thế giới.', 'assets/img/danang.jpg', 3299000, 10, 6),
(27, 'Du Lịch Vũng Tàu', '2021-07-19', 2, 'Du Lịch Vũng Tàu - Đến với Vũng Tàu bạn sẽ có cảm giác bình yên, dễ chịu với những con đường rộng rãi, thoáng đãng. Dưới là biển xanh, trên là những ngọn núi to, núi nhỏ, cùng những ngôi chùa thanh tịnh… Tất cả tạo nên một Vũng Tàu đầy ma lực, một thành phố Vũng Tàu không chỉ hiền hòa bình dị mà vô vàn những danh lam thắng cảnh nổi tiếng. Chương tình Du Lịch Vũng Tàu 2 ngày 1 đêm dịp lễ 30/4 & 1/5 của công ty Du Lịch Việt chúng tôi du khách sẽ được đến với suối nước nóng Bình Châu, ngâm mình thư giãn trong dòng suối trong veo ấm áp. Khám phá vẻ đẹp hoang sơ của biển Hồ Cốc. Hay leo 1.000 bậc thang để tham quan Tượng Đức Chúa dang tay nổi tiếng của thành phố biển Vũng Tàu.', 'assets/img/vungtau.jpg', 1999000, 10, 5),
(28, 'Du lịch Bình Thuận', '2021-07-20', 2, 'Tour Phan Thiết - Hòn Rơm - Mũi Né - Lâu đài rượu vang sẽ điểm dừng chân đầy thú vị cho du khách trong những ngày nghỉ cuối tuần của mình. Với làn nước biển trong xanh, những con sóng hiền hòa vỗ về trên bờ cát trải dài….mang đến cho bạn một cảm giác thanh bình đến khó tả. ', 'assets/img/binhthuan.jpg', 1499000, 10, 5),
(29, 'Du lịch Cao Cấp Quy Nhơn - Bình Định - KDL Hầm Hồ - Kỳ Co', '2021-07-21', 3, 'Du khách đến thiên đường nghỉ dưỡng phố biển Quy Nhơn (Bình Định) sẽ có cơ hội khám phá ghềnh đá ven biển với muôn hình kỳ thú. Với hành trình này, Quý khách sẽ có cả một danh sách dài các thiên đường đảo và biển chờ bạn khám phá tại tour Quy Nhơn giá rẻ…\r\nKhám phá Eo Gió, một “Jeju” của Việt Nam với vẻ đẹp thơ mộng của biển xanh và những rặng núi đá chập chùng. Nằm cách trung tâm thành phố Quy Nhơn 20 km, vào trung tâm xã Nhơn Lý chạy theo quốc lộ 19B, Eo Gió hiện ra trước mắt với hàng phi lao cùng những bãi cát trắng trải dài... Eo Gió sở hữu vẻ đẹp nên thơ hữu tình với những dãy núi chập chùng ôm trọn biển xanh, dưới chân núi là đường đi quanh co, đep chẳng khác gì những khung hình thường thấy qua những bộ phim Hàn Quốc. Đây là địa điểm cho ra đời cả nghìn tấm ảnh check in đẹp mê hồn.', 'assets/img/binhdinh.jpg', 3690000, 10, 7),
(30, 'Du lịch Quảng Nam', '2021-07-22', 4, 'Dải đất miền Trung được thiên nhiên ưu ái ban tặng cho những danh thắng đẹp trên cả tuyệt vời. Du lịch miền Trung, bạn sẽ bước chân qua những cung đường đèo uốn lượn đầy mộng mơ, ngắm nhìn những ngọn núi hùng vĩ, những bãi biển đẹp như tranh vẽ… Không chỉ có thế, đi tour miền Trung trong dịp đầu năm, bạn còn có cơ hội đến các điểm du lịch tâm linh, chắc chắn sẽ mang đến cho bạn những cảm giác mới mẻ khó quên,... Bởi gần đây, Tour lễ chùa đầu năm đã trở thành nét văn hóa đặc trưng không thể thiếu mỗi dịpTết đến Xuân về của từng du khách nói riêng và nhiều người dân Việt Nam nói chung. Và dịp nghỉ Tết Âm lịch cũng là cơ hội cho nhiều gia đình đi du lịch kết hợp hành hương, chiêm bái trở nên rất phổ biến hiện nay.', 'assets/img/quangnam.jpg', 8599000, 10, 6),
(31, 'Du lịch Hè - Tour Du lịch Đắklắk - Thác D’ray nur - Làng Cà Phê Trung Nguyên.', '2021-07-23', 3, 'Nổi tiếng với vẻ đẹp hoang dại của vùng đất đại ngàn, với thác nước trắng xóa và hồ nước đẹp mênh mang trong ánh nắng hoàng hôn, đến với Buôn Mê Thuột, du khách sẽ được khám phá thác D’ray Nur, tham quan Làng cà phê Trung Nguyên, ngắm nhìn không gian kiến trúc độc đáo mang đậm bản sắc của dân tộc Tây Nguyên và tận hưởng không gian cà phê đặc sắc, nhâm nhi những ly cà phê đậm đà, thơm ngon vào hàng bậc nhất. Thăm Buôn Ako D’Hông, du khách sẽ được hòa mình không gian văn hóa Cồng Chiêng Tây Nguyên được UNESCO công nhận là Kiệt tác truyền khẩu và phi vật thể nhân, qua đó tìm hiểu thêm về những phong tục, tập quán đầy thú vị của đồng bào dân tộc thiểu số nơi đây.', 'assets/img/daklak.jpg', 2599000, 10, 7),
(37, 'Du lịch Hè - Tour Du lịch Mỹ Tho - Cồn Phụng - Cần Thơ', '2021-07-25', 2, 'Du lịch Hè  - Du lịch Miền Tây - Xuôi theo dòng nước du khách đến với chợ nổi Cái Răng để chứng kiến điều kì lạ, chợ mà nổi được trên sông? Du khách đi chợ nổi Cái Răng trên sông Cần Thơ không chỉ để ngắm nhìn những ghe thuyền đầy ắp hoa quả, hàng hóa mà còn để thưởng thức hương vị tô hủ tiếu hay tô bún cua thơm ngon giữa dòng nước mênh mông. Bên cạnh tham quan các Chợ nổi, du khách còn được viếng thăm Thiền Viện Trúc Lâm Phương Nam, Thiền Viện được xem là một trong những công trình nghệ thuật độc đáo của đất Tây Đô - ngôi chùa rộng nhất khu vực Đồng bằng sông Cửu Long, được xây dựng trên diện tích gần 4 ha, với kết cấu lợp ngói, khung cột gỗ lim, chánh điện rộng cùng nhà tổ, nhiều tượng Phật được làm bằng gỗ Du Sam. ', 'assets/img/mytho.jpg', 1499000, 10, 6),
(38, 'Du lịch Miền Trung - Phú Yên - Tuy Hòa - Bãi Xếp', '2021-07-26', 3, 'Hành trình du lịch Phú Yên 3 ngày 2 đêm - Phú Yên đẹp tựa một cô thôn nữ, có nét chân chất, mộc mạc của làng quê Việt Nam lại ẩn chứa những nét nhẹ nhàng, thuần khiết, trong trẻo. Vẻ đẹp của Phú Yên đi cùng với những câu hát tuổi thơ “Bóng trăng trắng ngà, có cây đa to, có thằng Cuội già, ôm một mối mơ” trong bộ phim “Tôi thấy hoa vàng trên cỏ xanh” do đạo diễn Victor Vũ chắp tay thực hiện, đã làm lay động trái tim của biết bao nhiêu khán giả. Cũng chính bởi lẽ đó, tour du lịch Phú Yên tết Âm lịch 2021 đã trở thành một điểm đến lí tưởng trong lòng rất nhiều du khách.', 'assets/img/phuyen.jpg', 6099000, 10, 8),
(39, 'Du lịch Đà Nẵng - Huế - Thánh Địa La Vang - Động Phong Nha', '2021-07-28', 3, 'Du lịch Miền Trung được thiên nhiên đặc biệt ưu đãi, mảnh đất miền Trung đẹp với nhiều dãy núi hùng vỹ xanh rì, những bờ biển cát trắng mịn thoai thoải và những dòng sông trong vắt thơ mộng. Không những vậy, trên con đường di sản miền Trung cùng Du Lịch Việt, du khách còn được thưởng ngoạn những di sản thế giới cuả Việt Nam đó là Phố cổ Hội An – nơi bến cảng một thời sầm uất nhất Đông Dương, quần thể di tích Cố Đô Huế với hệ thống đền đài lăng tẩm nguy nga tráng lệ và Động Thiên Đường với nhiều hang động kì bí của tạo hóa.', 'assets/img/danang1.jpg', 8999000, 10, 7),
(40, 'Du lịch Đà Nẵng - Ngũ Hành Sơn - Bà Nà - Phố Cổ Hội An', '2021-07-30', 3, 'Du lịch Đà Nẵng hành trình khám phá thành phố biển sầm uất nhất dải đất Miền Trung nắng gió. Tham quan Ngũ Hành Sơn phong cảnh hữu tình với hệ thống các hang động, chùa chiền linh thiêng cổ kính. Một ngày vi vu trên cao nguyên Bà Nà để khám phá những tòa lâu đài mang đường lối kiến trúc Châu Âu. Xuôi về Quảng Nam, du khách sẽ phải ngỡ ngàng trước một Hội An yên bình nằm tọa lạc bên sông Thu Bồn. Phố Cổ Hội An hấp dẫn du khách bởi những dãy nhà màu vàng, mái ngói nằm san sát nhau, những cửa hàng truyền thống và ẩm thực phong phú. Đặc biệt trong tour du lịch Đà Nẵng này, du khách còn được trực tiếp xem show diễn Ký Ức Hội An. Đây là show diễn duy nhất của Việt Nam được công chiếu tại Quảng Trường Thời Đại - New York. Và được báo chí quốc tế ghi nhận là show diễn thực cảnh đẹp nhất thế giới.', 'assets/img/danang2.jpg', 3299000, 10, 5),
(41, 'Du lịch Hè - Tour Du lịch Quy Nhơn - Phú Yên', '2021-07-28', 4, 'Du lịch Hè - Du lịch Phú Yên là vùng đất có cảnh quan thiên nhiên non nước hữu tình, không ồn ào náo nhiệt như những điểm tham quan du lịch khác, nơi đây khoác lên mình nét đẹp vừa hoang sơ vừa bình dị, như một nét duyên ngầm đã níu giữ biết bao du khách. Những du khách đã từng du lịch Phú Yên thì tất cả đều bị chinh phục bởi những địa danh đẹp và nổi tiếng, cùng những món ăn đặc sản địa phương cũng như những con người giản dị,mộc mạc, thật thà chất phác nơi đây.', 'assets/img/phuyen1.jpg', 5699000, 10, 6),
(42, 'Du lịch Nha Trang - Tháp Bà Ponagar - Chùa Long Sơn ', '2021-07-29', 3, 'Du lịch Nha Trang - Tháp Bà Ponagar - Chùa Long Sơn - Vinpearl Land dịp Lễ 30/4 từ Sài Gòn - Du lịch Nha Trang - Thành phố biển Nha Trang nổi tiếng với những cảnh quan thiên nhiên đẹp “mê hoặc” lòng người, hàng năm thu hút hàng trăm ngàn du khách cả trong và ngoài nước đến thăm quan nghỉ dưỡng. Nếu bạn đang tìm kiếm một chuyến du lịch đúng nghĩa nghỉ dưỡng thì Tour du lịch Nha Trang là sự lựa chọn tuyệt vời dành cho bạn. Đến với Thành phố biển Nha Trang bạn sẽ được thăm quan ngắm cảnh với rất nhiều những danh lam thắng cảnh nổi tiếng, được thử trải nghiệm câu tôm trên thuyền khi mặt trời đã ngả bóng,... Được thưởng thức nhiều món ăn hấp dẫn, cùng khí hậu mát mẻ,... Hứa hẹn đây sẽ là một kỳ nghỉ đầy thú vị và ý nghĩa dành cho bạn.', 'assets/img/nhatrang.jpg', 5999000, 10, 6),
(43, 'Du lịch Nha Trang - Du Ngoạn 4 Đảo - Vinpearl Land - Tháp Bà Ponagar - Nhà Thờ Đá', '2021-07-27', 4, 'Du lịch Nha Trang - Du Ngoạn 4 Đảo - Vinpearl Land - Tháp Bà Ponagar - Nhà Thờ Đá dịp Lễ 30/4 từ Sài Gòn - Du lịch Nha Trang - Thành phố biển Nha Trang nổi tiếng với những cảnh quan thiên nhiên đẹp “mê hoặc” lòng người, hàng năm thu hút hàng trăm ngàn du khách cả trong và ngoài nước đến thăm quan nghỉ dưỡng. Nếu bạn đang tìm kiếm một chuyến du lịch đúng nghĩa nghỉ dưỡng thì Tour du lịch Nha Trang là sự lựa chọn tuyệt vời dành cho bạn. Đến với Thành phố biển Nha Trang bạn sẽ được thăm quan ngắm cảnh với rất nhiều những danh lam thắng cảnh nổi tiếng, được thử trải nghiệm câu tôm trên thuyền khi mặt trời đã ngả bóng,... Được thưởng thức nhiều món ăn hấp dẫn, cùng khí hậu mát mẻ,... Hứa hẹn đây sẽ là một kỳ nghỉ đầy thú vị và ý nghĩa dành cho bạn.', 'assets/img/nhatrang2.jpg', 4399000, 10, 7),
(44, 'Du lịch Buôn Ma Thuột - Pleiku - Kon Tum khu du lịch Măng Đen ', '2021-07-27', 4, 'Du lịch Buôn Ma Thuột- Pleiku - Kon Tum khu du lịch Măng Đen từ Sài Gòn 2021.Nổi tiếng với vẻ đẹp hoang dại của vùng đất đại ngàn, với thác nước trắng xóa và hồ nước đẹp mênh mang trong ánh nắng hoàng hôn, đến với Buôn Mê Thuột, du khách sẽ được khám phá thác D’ray Nur, tham quan Buôn Đôn, ngắm nhìn không gian kiến trúc độc đáo mang đậm bản sắc của dân tộc Tây Nguyên và tận hưởng không gian cà phê đặc sắc, nhâm nhi những ly cà phê đậm đà, thơm ngon vào hàng bậc nhất. Thăm biển hồ T’Nưng – đôi mắt Pleiku, với những hàng thông cổ thụ tuyệt đẹp, cánh đồng chè trải dài xanh ngát. Viếng Đức mẹ Măng Đen, nơi được mệnh danh là Đà lạt thứ 2…', 'assets/img/daklak1.jpg', 3499000, 10, 5),
(45, 'Tour Limousine Phú Yên 3N3D: Điệp Sơn - Cực Đông Tổ Quốc - Cao Nguyên Vân Hòa', '2021-07-25', 3, 'Phú Yên là một tỉnh ven biển thuộc duyên hải Nam Trung Bộ, phía bắc giáp Bình Định, phía nam giáp Khánh Hòa. Với 3 mặt giáp núi, 1 mặt giáp biển đông. Nằm ở phía đông dãy Trường Sơn, Phú Yên được thiên nhiên ưu đãi nhiều danh lam thắng cảnh. Đây là Núi Nhạn sừng sững giữa thành phố Tuy Hòa, bên cạnh là dòng sông ba hiền hòa ngày ngày bồi đắp phù sa cho vựa lúa Miền Trung; kia là gành đá đĩa như một tạo hóa kỳ lạ của thiên nhiên ban tặng cho con người, còn đó Vịnh Vũng Rô với chiến tích của những con tàu không số. Mời các Quý khách đến với Phú Yên với những danh lam thắng cảnh và lòng mến khách của người dân xứ nẫu. Cùng iVIVU khám phá ngay hôm nay!  ', 'assets/img/phuyen2.jpg', 6280000, 10, 6),
(48, 'Du lịch Hè - Tour Du lịch Quy Nhơn - Phú Yên 4N3Đ từ Sài Gòn', '2021-09-01', 4, 'Du lịch Hè - Du lịch Phú Yên là vùng đất có cảnh quan thiên nhiên non nước hữu tình, không ồn ào náo nhiệt như những điểm tham quan du lịch khác, nơi đây khoác lên mình nét đẹp vừa hoang sơ vừa bình dị, như một nét duyên ngầm đã níu giữ biết bao du khách. Những du khách đã từng du lịch Phú Yên thì tất cả đều bị chinh phục bởi những địa danh đẹp và nổi tiếng, cùng những món ăn đặc sản địa phương cũng như những con người giản dị,mộc mạc, thật thà chất phác nơi đây.', 'assets/img/du-lich-he-du-lich-phu-yen-du-lich-viet(1).jpg', 5699000, 10, 5),
(49, 'Du lịch Hè - Tour Du lịch Côn Đảo 3N2Đ từ Sài Gòn giá tốt', '2021-09-02', 3, '', 'assets/img/du-lich-con-dao-gia-tot-du-lich-viet.jpg', 7950000, 10, 7),
(50, 'Du lịch Hè - Tour Du lịch Hà Giang - Quản Bạ - Đồng Văn - Lũng Cú - Sông Nho Quế từ Sài Gòn', '2021-09-03', 4, 'Du lich He - Tour Du lịch Hà Giang nổi tiếng với phong cảnh hùng vĩ cùng với mùa lúa chín nhuộm vàng lên tận cả chân mây, những thửa ruộng bậc thang ngút tầm mắt hay những cánh đồng hoa tam giác mạch phủ tím những quả đồi, hòa quyện với bản sắc văn hóa dân tộc vùng cao là những điều thu hút mọi du khách trong và ngoài nước đến du lịch Hà Giang trong thời gian qua. Hãy cùng Du Lịch Việt khám phá những điểm đến hấp dẫn không thể bỏ qua đến vùng đất tươi đẹp này.', 'assets/img/du-lich-tet-tham-quan-thac-ban-gioc-du-lich-viet.jpg', 6990000, 10, 7),
(51, 'Du lịch Hè - Tour Du lịch Vịnh Hạ Long - Yên Tử - Sa Pa - Bản Cát Cát từ Sài Gòn', '2021-09-04', 4, 'Du lịch Hè - Tour Du lịch Vịnh Hạ Long - Yên Tử - Sapa - Bản Cát Cát từ Sài Gòn - Miền Bắc là nơi khởi nguồn văn hóa ngàn năm văn hiến của dân tộc Việt Nam. Du lịch miền Bắc du khách sẽ được khám phá những thắng cảnh thiên nhiên đẹp mê hồn cùng nhiều công trình kiến trúc ấn tượng được tạo nên bởi bàn tay khéo léo của con người. Cùng Du Lịch Việt tìm hiểu những địa điểm du lịch miền bắc hấp dẫn nhất như Hà Nội - Hạ Long,... để bắt đầu lên kế hoạch cho chuyến du lịch ngay nhé!', 'assets/img/du-lich-ha-long-gia-re.jpg', 7199000, 10, 7),
(52, 'Du lịch Hè - Tour Du lịch Hà Nội - Lào Cai - Sa Pa - Bản Cát Cát từ Sài Gòn', '2021-09-05', 4, 'Du lịch Hè - Tour Du lịch Hà Nội - Lào Cai - Sa Pa - Bản Cát Cát từ Sài Gòn. Du lịch Sapa - Sự lựa chọn hàng đầu của những du khách muốn khám phá xứ sở sương mù. Nơi đây còn được du khách gọi bằng cái tên thân quen \"thiên đường du lịch Sapa\". Sapa không chỉ thu hút du khách về mùa đông mà còn hấp dẫn du khách về mùa xuân. Thành phố Sapa chìm trong làn mây bồng bềnh huyền ảo, vẽ nên một bức tranh sơn thủy hữu tình. Một dáng vẻ trầm mặc, uy nghi pha chút lãng mạn thơ mộng, quý khách sẽ tìm thấy tất cả những cảm nhận ấy khi được tận mắt ngắm nhìn SaPa.', 'assets/img/ban-cat-cat-tour-tham-quan-sapa.jpg', 6699000, 10, 6),
(53, 'Du lịch Hè - Tour Du lịch Hà Nội - Đền Đô - Yên Tử - Vịnh Hạ Long - Sa Pa từ Sài Gòn', '2021-09-06', 5, 'Du lịch Hè - Tour Du lịch Hà Nội - Đền Đô - Yên Tử - Vịnh Hạ Long - Sa Pa từ Sài Gòn. Hành trình phương Bắc sẽ đưa du khách đến Hà Nội 36 phố phường, nếm đặc sản đất Hà Thành, chìm đắm trong nên thơ cảnh sắc núi rừng Yên Tử, cảm nhận không gian bao la hùng vĩ của Hạ Long, khám phá nét đặc sắc của văn hóa vùng cao nơi phố núi Sapa với mênh mông những thửa ruộng bậc thang xanh ngút ngàn và tìm hiểu đời sống văn hóa của đồng bào dân tộc – Đó là những gì mà du khách cảm nhận được trong 5 ngày 4 đêm, chương trình “Hà Nội – Yên Tử – Hạ Long – Lào Cai – Sapa” của Du Lịch Việt.', 'assets/img/du-lich-sapa-dip-le-30-4-du-lich-viet(1).jpg', 7599000, 10, 8),
(54, 'Du lịch Hè - Tour Du lịch Phan Thiết - Hòn Rơm - Mũi Né - Lâu Đài Rượu Vang từ Sài Gòn', '2021-09-07', 2, 'Tour Phan Thiết - Hòn Rơm - Mũi Né - Lâu đài rượu vang sẽ điểm dừng chân đầy thú vị cho du khách trong những ngày nghỉ cuối tuần của mình. Với làn nước biển trong xanh, những con sóng hiền hòa vỗ về trên bờ cát trải dài….mang đến cho bạn một cảm giác thanh bình đến khó tả. ', 'assets/img/thap-cham-poshanu-tour-phan-thiet-mua-thu.jpg', 1499000, 10, 8),
(55, 'Du lịch Hè - Tour Du lịch Tây Ninh - Tòa Thánh Cao Đài - Vinpearl - Núi Bà Đen từ Sài Gòn', '2021-09-08', 2, 'Du lịch Tây Ninh tuy chưa thực sự hấp dẫn so với địa phương khác trong cả nước nhưng vẫn có nhiều nét riêng hấp dẫn thu hút du khách khắp nơi, Không chỉ được biết tới với núi Bà Đen hay tòa thánh Cao Đài, du lịch Tây Ninh vẫn hấp dẫn với những núi, hồ, chùa ..hãy cùng Du Lịch Việt khám phá những khung cảnh thiên nhiên đầy quyến rũ.', 'assets/img/du-lich-tay-ninh-di-tuyen-cap-treo-nui-ba-den-du-lich-viet.jpg', 1799000, 10, 7),
(56, 'Du lịch Hè - Tour Du lịch Cà Mau - Bạc Liêu - Sóc Trăng từ Sài Gòn', '2021-09-09', 2, 'Du lịch Hè - Tour Du lịch Cà Mau - Bạc Liêu - Sóc Trăng từ Sài Gòn - Du lịch Miền Tây - Cà Mau - Bạc Liêu - Sóc Trăng 2 ngày 2 đêm từ Sài Gòn 2020 - Du khách sẽ được chu du miền Đất Mũi, nơi cuối cùng của tổ quốc để cảm nhận hết cái \"chất\" của vùng Đồng Bằng Sông Cửu Long rộng lớn với hệ sinh thái rừng ngập mặn rất đa dạng, ngắm cảnh thiên nhiên vả thưởng thức nhiều đặc sản hấp dẫn của mảnh đất địa cuối Tổ Quốc.', 'assets/img/ca-mau-tour-du-lich-can-tho-mua-thu.jpg', 1799000, 10, 6),
(57, 'Du lịch Hè - Tour Du lịch Cam Ranh - Đảo Bình Ba từ Sài Gòn', '2021-09-10', 2, 'Du lịch Đảo Bình Ba - Đảo Bình Ba có lẽ đó là một cái tên khá lạ lẫm đối với nhiều người, nhưng đối với dân du lịch bụi hay phượt thì đây là một điểm du lịch rất tuyệt vời. Bình Ba là một hòn đảo nhỏ thuộc Cam Ranh, Khánh Hòa, diện tích trên 3km vuông, cách Nha Trang 60km, cách thành phố Hồ Chí Minh khoảng 390km. Để đến được Bình Ba bạn phải đến cảng Ba Ngòi. Đây là một hòn đảo quân sự, trước đây cấm dân ngoài và người nước ngoài vào đảo nhưng bây giờ đã cho phép người Việt Nam đến nhưng vẫn cấm người nước ngoài, nhưng với điều kiện là không mò đến những khu vực cấm và không có những hành động quá khích. Chính vì điều này của Bình Ba, khiến cho nhiều người càng muốn thực hiện tour du lịch đảo Bình Ba để hiểu về nó nhiều hơn. Và chuyến du lịch đảo Bình Ba sẽ không làm bạn thất vọng bởi vẻ đẹp hoang sơ kỳ bí của nó.', 'assets/img/tour-du-lich-dao-binh-ba-mua-thu.jpg', 2099000, 10, 8),
(58, 'Du lịch Hè - Tour Du lịch Đà Lạt - Thiền Viện Trúc Lâm - Kim Ngân Hills Resort từ Sài Gòn', '2021-09-11', 3, 'Du lịch Hè - Tour Du lịch Đà Lạt - Thiền Viện Trúc Lâm - QUÊ Garden - Kim Ngân Hills Resort từ Sài Gòn. Khi tham gia tour cùng Du Lịch Việt ngoài việc thưởng lãm vẻ đẹp đặc sắc của các loài hoa, khung cảnh thơ mộng với khí trời se lạnh của vùng đất phố núi, Quý khách sẽ được cảm nhận Đà Lạt chính là địa điểm du lịch đi hoài không chán bởi những “tọa độ sống ảo đẹp không góc chết” mang những phong cách hoàn toàn riêng biệt. Nếu như Đà Lạt View Coffee nổi danh với hình ảnh “Cổng trời thứ 2” phá cách và độc đáo, thì Quê Garden cũng đang làm mưa làm gió trên các trang mạng xã hội, là một khu vườn với lối kiến trúc Nhật Bản nằm giữa cao nguyên Lâm Viên. Cuối cùng là ngôi làng cổ kính Châu Âu - Kim Ngân Hills với cả bầu trời thú cưng hiền lành cùng khu ẩm thực đặc biệt mang dấu.', 'assets/img/tua-da-lat-kham-pha-kim-ngan-hills-gia-tot-du-lich-viet.jpg', 2099000, 10, 5),
(60, 'Du lịch Hè - Tour Du lịch Phú Quốc - Grand World - Vinwonders từ Sài Gòn', '2021-09-12', 3, 'Du lịch Hè - Tour Du lịch Phú Quốc - Phú Quốc được xem như ngọc đảo của Việt Nam, nơi có rừng nguyên sinh và hệ động thực vật phong phú. Hơn hết, Phú Quốc còn nổi tiếng với biển xanh – cát trắng – nắng vàng tuyệt đẹp. Một vài điểm đến thú vị nhất ở thiên đường biển bạn không thể bỏ lỡ.', 'assets/img/du-lich-phu-quoc-du-lich-bien-du-lich-viet(2).jpg', 4099000, 10, 6),
(61, 'Du lịch Hè - Tour Miền Bắc Hà Nội - Hà Giang - Lũng Cú - Cao Bằng - Thác Bản Giốc - Sông Nho Quế từ Sài Gòn', '2021-09-13', 5, 'Du lich He - Tour Du lịch Hà Giang nổi tiếng với phong cảnh hùng vĩ cùng với mùa lúa chín nhuộm vàng lên tận cả chân mây, những thửa ruộng bậc thang ngút tầm mắt hay những cánh đồng hoa tam giác mạch phủ tím những quả đồi, hòa quyện với bản sắc văn hóa dân tộc vùng cao là những điều thu hút mọi du khách trong và ngoài nước đến du lịch Hà Giang trong thời gian qua. Hãy cùng Du Lịch Việt khám phá những điểm đến hấp dẫn không thể bỏ qua đến vùng đất tươi đẹp này.', 'assets/img/du-lich-tet-kham-pha-cot-co-lung-cu-du-lich-viet.jpg', 7499000, 10, 5),
(62, 'Du lịch Hè - Tour Du lịch Đồng Tháp từ Sài Gòn', '2021-09-13', 2, 'THAM QUAN LÀNG HOA SA ĐÉC:  Làng hoa Sa Đéc là nơi cung cấp hoa cho thành phố Hồ Chí Minh, và hầu hết các tỉnh thành Nam Bộ. Một lần được đứng giữa không gian hoa rực rỡ sắc màu và hòa mình vào không khí nhộn nhịp của những người nông dân Sa Đéc du khách sẽ cảm nhận được hơi thở mùa xuân len lỏi vào từng nhịp sống. Các loài hoa đua nhau tỏa hương khoe sắc trong ánh nắng vàng nhè nhẹ gió xuân như đón chào lữ khách ghé thăm.  \r\n\r\nTHAM QUAN VƯỜN QUÝ HỒNG LAI VUNG: Đến Lại Vùng vương quốc của quýt hồng, hòa mình với những vườn quýt hồng trĩu quả du khách sẽ có những trải nghiệm vô cùng thú vị trong chuyến hành hương đầu năm của mình. Đến đây, khách sẽ thấy quýt đỏ rợp vườn, cây nào cũng trĩu quả, no tròn và mọng nước. Ấn tượng đầu tiên khi du khách sẽ rất bỡ ngỡ vì quýt ở đây rất nhiều. Trái Quýt có màu vàng cam, bóng tròn và săn chắc. Đến đây du khách sẽ có dịp trải nghiệm cảm giác được làm nông dân của một thủ phủ quýt hồng nức tiếng.', 'assets/img/lang-hoa-sa-dec-tour-du-lich-mien-tay-gia-tiet-kiem.jpg', 1499000, 10, 6),
(63, 'Du lịch Hè - Tour Du lịch Miền Tây - Mỹ Tho - Châu Đốc - Thiên Cấm Sơn từ Sài Gòn', '2021-09-14', 3, 'Du lịch Miền Tây - Mang vẻ đẹp đặc trưng của vùng đồng bằng sông nước Cửu Long, An Giang nổi tiếng bởi cảnh quan thiên nhiên nhuốm màu sắc huyền bí. Nằm trong dãy núi Thất Sơn, núi Cấm là ngọn núi cao nhất, nơi đây cảnh trí sơn thủy hữu tình, khí hậu mát mẻ được ví như một phiên bản “Đà Lạt” của miền Tây, là điểm đến lý tưởng du lịch sinh thái và tâm linh cho du khách hành hương cả nước. Hành trình sẽ là chuyến du hí đầy màu sắc dành cho du khách.', 'assets/img/mieu-ba-chua-su-tour-du-lich-chau-doc.jpg', 2199000, 10, 6),
(64, 'Du lịch Hè - Tour Du lịch Đảo Bình Hưng - Ninh Chữ từ Sài Gòn', '2021-09-15', 3, 'Đến với hành trình du lịch Đảo Bình Hưng du khách sẽ bị cuốn hút ngay bởi một màu xanh và trong của nước biển hòa quyện bên những bãi cát trắng tinh. Những bãi biển ở đây không dài và rộng nhưng luôn sạch và trong tới tận đáy. Đặc biệt, đên đây du khách có thể thoải mái ngắm nhìn những dải san hô chỉ với vài bước chân trần…', 'assets/img/bai-nuoc-ngot-du-lich-dao-binh-hung-mua-thu.jpg', 2199000, 10, 8),
(65, 'Du lịch Hè - Tour Du lịch Đảo Nam Du từ Sài Gòn', '2021-09-16', 2, 'bán chương trình tour Đảo Nam Du giá rẻ. Với hành trình này du khách còn có dịp thưởng thức nhiều món ngon vật lạ, mỗi món ăn đều có những hương vị đặc trưng vùng biển đảo. Ngoài ra, hành trình không chỉ hấp dẫn du khách bởi cái hoang sơ của biển, sự chân chất thật thà của người dân mà còn hấp dẫn du khách phương xa bởi sự ngọt ngào của \"Vịnh Hạ Long phương nam\".', 'assets/img/tour-dao-nam-du-gia-re.jpg', 2299000, 10, 7),
(66, 'Du lịch Hè - Tour Du lịch Phan Thiết - Mũi Né - Đồi Cát Vàng Bay từ Sài Gòn', '2021-09-17', 3, 'Du lịch Hè - Tour Du lịch Phan Thiết - Mũi Né - Đồi Cát Vàng Bay từ Sài Gòn. Du lịch Phan thiết được biết đến với hình ảnh những bãi biển trong xanh, những đồi cát bay trải dài, những hòn đảo lớn nhỏ. Một chuyến du lịch Phan Thiết ắt hẳn sẽ làm du khách tour Phan Thiết thích thú. Tìm đến một chốn thư giãn, quên đi những mệt mỏi muộn phiền trong cuộc sống, Phan Thiết chính là điểm đến giúp chúng ta có những được điều đó.', 'assets/img/du-lich-phan-thiet-kham-pha-doi-cat-vang-gia-tot-du-lich-viet.jpg', 2499000, 10, 8),
(68, 'Du lịch Hè - Tour Du lịch Nha Trang - Đảo Điệp Sơn - Vinpearland - Nhà Thờ Đá từ Sài Gòn', '2021-09-19', 3, 'Du lịch đảo Điệp Sơn, trải nghiệm cảm giác đi bộ trên con đường nổi giữa đại dương, thưởng thức hải sản ở làng chài Vạn Giã… sẽ đem lại cho bạn những cảm giác mới mẻ, đầy thú vị. Nơi đây cuốn hút bởi vẻ đẹp hoang sơ và đặc biệt là con đường nổi lênh đênh giữa đại dương độc đáo nhất Việt Nam.', 'assets/img/vinpearland-tour-du-lich-nha-trang.jpg', 2799000, 10, 8),
(69, 'Du lịch Hè - Tour Du lịch Khám phá đảo Nam Du 3 ngày từ Sài Gòn', '2021-09-20', 3, 'Du lịch Miền Tây - Chương trình tham quan Đảo Nam Du 3 ngày 3 đêm lần này sẽ đưa du khách ghé thăm những địa điểm tuyệt đẹp nhất của đảo Nam Du, lặn ngắm san hô, khám phá thế giới thủy cung huyền ảo, tham quan Dốc Ông Tình, chinh phục hải đăng Nam Du - nơi cao nhất của đảo chứa đựng nhiều câu chuyện thú vị... \r\n \r\n', 'assets/img/tour-du-lich-dao-nam-du-mua-thu(1).jpg', 2799000, 10, 5),
(70, 'Du lịch Hè - Tour Du lịch Mỹ Tho - Cần Thơ - Cà Mau - Bạc Liêu - Sóc Trăng từ Sài Gòn', '2021-09-21', 4, 'Du lịch Hè - Tour Du lịch Mỹ Tho - Cần Thơ - Cà Mau - Bạc Liêu - Sóc Trăng từ Sài Gòn - Du lịch Mỹ Tho - Xuôi về Miền Tây, Du Khách có dịp trải nghiệm thú vị khi hòa mình vào cuộc sống miệt vườn, thưởng thức những món ăn đặc sản đậm đà hương vị của vùng đồng bằng cây ngọt trái lành. Đến Cần Thơ, vùng đất mệnh danh là “Tây Đô”, lên thuyền du ngoại chợ nổi Cái Răng, trực tiếp trải nghiệm nét văn hóa độc đáo trên sông nước. Ghé Sóc Trăng, thăm Chùa Dơi hơn 400 năm tuổi, vừa chiêm ngưỡng lối kiến trúc Khơmer độc đáo vừa nghe kể về đàn dơi huyền bí trụ ngụ trong khuôn viên chùa. Đặt chân đến Đất Mũi, vùng đất cuối cùng của Tổ quốc, đứng trên đài quan sát, Du Khách sẽ thu vào tầm mắt khung cảnh rừng mắm, rừng đước xanh rì nối nhau, trải dài ngút ngàn, tiếp giáp với mây trời. Đến Bạc Liêu, khám phá miền quê hương của chàng Công Tử tiêu tiền như nước, nghe nhắc lại những giai thoại nức tiếng gần xa trong quá khứ về sự xa hoa. Ngoài ra, Du khách còn được tìm hiểu về điệu Dạ Cổ Hoài Lang, thăm những di tích lịch sử, điểm du lịch sinh thái và đặc biệt là những điểm du lịch tâm linh gắn liền với cảnh quan và các câu chuyện cổ xưa.', 'assets/img/chua-vinh-trang-du-lich-can-tho-gia-re.jpg', 2999000, 10, 6),
(71, 'Du lịch Hè - Tour Du lịch Nha Trang - Vinpearland - Đảo Bình Hưng từ Sài Gòn', '2021-09-22', 4, 'Du lịch Hè - Tour Du lịch Nha Trang – Đảo Bình Hưng là hành trình dành cho những du khách thích khám phá biển đảo. Với hành trình du khách sẽ được khám phá đảo Bình Hưng một viên ngọc vô cùng quyến rũ bởi nét đẹp hoang sơ chưa được mài đũa, kết hợp với hành trình khám phá thành phố biển Nha Trang nổi tiếng là thiên đường du lịch biển Việt Nam.', 'assets/img/hai-dang-hon-chut-tour-du-lich-dao-binh-hung.jpg', 2999000, 10, 6),
(72, 'Du lịch Hè - Tour Du lịch Sa Pa - Bản Cát Cát - Chinh Phục Đỉnh Núi Fansipan từ Sài Gòn', '2021-09-24', 4, 'Du lịch Hè - Tour Du lịch Sa Pa - Bản Cát Cát - Chinh Phục Đỉnh Núi Fansipan từ Sài Gòn - Du lịch Sapa - Sự lựa chọn hàng đầu của những du khách muốn khám phá xứ sở sương mù. Nơi đây còn được du khách gọi bằng cái tên thân quen \"thiên đường du lịch Sapa\". Sapa không chỉ thu hút du khách về mùa đông mà còn hấp dẫn du khách về mùa xuân. Thành phố Sapa chìm trong làn mây bồng bềnh huyền ảo, vẽ nên một bức tranh sơn thủy hữu tình. Một dáng vẻ trầm mặc, uy nghi pha chút lãng mạn thơ mộng, quý khách sẽ tìm thấy tất cả những cảm nhận ấy khi được tận mắt ngắm nhìn SaPa.', 'assets/img/fansipan-du-lich-sapa-gia-re-du-lich-viet.jpg', 5199000, 10, 8),
(73, 'Du lịch Hè - Tour Du lịch Hà Nội - Yên Tử - Hạ Long - Tràng An - Sa Pa từ Sài Gòn', '2021-09-25', 5, 'Du lịch Hè - Tour Du lịch Hà Nội - Yên Tử - Hạ Long - Tràng An - Sa Pa từ Sài Gòn. Miền Bắc là nơi khởi nguồn văn hóa ngàn năm văn hiến của dân tộc Việt Nam. Du lịch miền Bắc du khách sẽ được khám phá những thắng cảnh thiên nhiên đẹp mê hồn cùng nhiều công trình kiến trúc ấn tượng được tạo nên bởi bàn tay khéo léo của con người. Điểm du lịch Tràng An là nơi du khách sẽ được khám phá một trong những địa điểm du lịch đẹp nhất Ninh Bình. Tạo hóa đã vô cùng ưu ái ban tặng cho nơi đây một cảnh quan thiên nhiên tuyệt đẹp với các dãy núi uốn lượn bao quanh các dòng Suối nước tự nhiên, tạo nên vô vàn các hang động kỳ ảo, huyền bí.... Cùng Du Lịch Việt khám phá những địa điểm du lịch miền bắc hấp dẫn nhất như Hà Nội - Yên Tử - Hạ Long - Chùa Bái Đính - Tràng An - Sapa - Bản Cát Cát - Đỉnh Fansipan,... để bắt đầu lên kế hoạch cho chuyến du lịch ngay nhé!', 'assets/img/du-lich-ha-long-gia-tiet-kiem.jpg', 8999000, 10, 7),
(74, 'Du lịch Cao Cấp Phú Quốc - Trải Nghiệm Du Thuyền Đẳng Cấp Sarita từ Sài Gòn', '2021-09-26', 3, 'Du lịch Cao Cấp cùng Du thuyền Sarita Phú Quốc, được mệnh danh là nàng công chúa Sarita đến từ nước Pháp xa xôi, mang vẻ đẹp tinh tế, thanh lịch và sang trọng đậm chất Pháp với những cánh buồm trắng vươn cao kiêu hãnh giữa biển xanh. Đây cũng là du thuyền 2 thân sang trọng đầu tiên tại Phú Quốc. Du thuyền Catamaran Sarita Phú Quốc sẽ đưa bạn khám phá cảnh đẹp của cáp treo dài nhất Đông Nam Á. Thuyền neo đậu tại vùng vịnh nối liền Bãi Khem – Mũi Ông Đội, bạn sẽ được tận hưởng làn gió nhiệt đới trong lành, check-in sống ảo. Hay thoải mái thưởng thức bữa trưa, buffet nhẹ trong ngày với những món ăn đặc sản ở Phú Quốc.', 'assets/img/tour-du-lich-cao-cap-phu-quoc-du-lich-viet.jpg', 21999000, 10, 7),
(75, 'Du lịch Đà Lạt - Thiền Viện Trúc Lâm - QUÊ Garden - Kim Ngân Hills Resort dịp Lễ 30/4 từ Sài Gòn', '2021-09-27', 3, 'Du lịch Đà Lạt - Thiền Viện Trúc Lâm - QUÊ Garden - Kim Ngân Hills Resort dịp Lễ 30/4 từ Sài Gòn. Khi tham gia tour cùng Du Lịch Việtngoài việc thưởng lãm vẻ đẹp đặc sắc của các loài hoa, khung cảnh thơ mộng với khí trời se lạnh của vùng đất phố núi, Quý khách sẽ được cảm nhận Đà Lạt chính là địa điểm du lịch đi hoài không chán bởi những “tọa độ sống ảo đẹp không góc chết” mang những phong cách hoàn toàn riêng biệt. Nếu như Đà Lạt View Coffee nổi danh với hình ảnh “Cổng trời thứ 2” phá cách và độc đáo, thì Quê Garden cũng đang làm mưa làm gió trên các trang mạng xã hội, là một khu vườn với lối kiến trúc Nhật Bản nằm giữa cao nguyên Lâm Viên. Cuối cùng là ngôi làng cổ kính Châu Âu - Kim Ngân Hills với cả bầu trời thú cưng hiền lành cùng khu ẩm thực đặc biệt mang dấu.', 'assets/img/tua-da-lat-kham-pha-kim-ngan-hills-gia-tot-du-lich-viet.jpg', 3599000, 10, 5),
(76, 'Du lịch Đà Lạt - QUÊ Garden - Cafe Mê Linh - Nông Trại Cún Puppy - Fairytale Land dịp Lễ 30/4 từ Sài Gòn', '2021-09-28', 4, 'Tour Lễ 30/4 Đà Lạt - QUÊ Garden - Đồi Chè Cầu Đất - Cafe Mê Linh - Nông Trại Cún Puppy - Fairytale Land - Du lịch Đà Lạt- Một thành phố mộng mơ,với vẻ đẹp lãng mạn không kém phần mơ mộng, Khách Du lịch Đà Lạt thường gọi nơi đây bằng nhiều cái tên :thành phố ngàn hoa, thành phố sương mù, phố núi Đà Lạt , hay tiểu Paris,mỗi cái tên đều chất chứa bao tình yêu.Tour du lịch Đà Lạt chính là điểm đến và cũng là một trong những điểm du lịch thú vị và đặc biệt nhất và là một điểm du lịch đối với những cặp đôi yêu nhau.Đà Lạt chính là thiên đường nghỉ dưỡng và du lịch được nhiều du khách yêu thích.', 'assets/img/du-lich-da-lat-vuon-hoa-cam-tu-cau-du-lich-viet.jpg', 4699000, 10, 8),
(77, 'Du lịch Đảo Bình Hưng - Ninh Chữ - Vườn Nho - Làng Gốm Bàu Trúc dịp Lễ 30/4 từ Sài Gòn', '2021-09-29', 3, 'Du lịch đảo Bình Hưng Ninh Chữ là một trong những hòn đảo thuộc Tứ Bình mang vẻ đẹp hoang sơ hiếm có. Đảo Bình Hưng nằm ẩn mình dưới chân đèo của cung đường biển Bình Tiên - Vĩnh Hy. Nơi đây được ví như \"hòn ngọc thô\" vì chưa được khai thác nhiều. Du khách đến đảo Bình Hưng sẽ được thưởng thức hải sản tươi ngon đặc biệt là tôm hùm. Trong tour du lịch đảo Bình Hưng Ninh Chữ du khách sẽ di chuyển bằng xe tuk tuk lên Hải Đăng Hòn Chút và ngằm toàn cảnh biển Bình Hưng xanh ngắt một vùng từ trên cao. Tham quan vườn nho ông Ba Mọi và làng gốm Chăm Bàu Trúc, một trong những làng nghề cổ xưa của người Chăm. Hãy cùng Du Lịch Việt trải nghiệm chuyến đi tuyệt vời này nhé.', 'assets/img/thap-cham-poklong-garai.jpg', 3699000, 10, 5),
(78, 'Du lịch Đà Nẵng - Bán Đảo Sơn Trà - Phố Cổ Hội An - Đại Nội - Huế dịp Lễ 30/4 từ Sài Gòn', '2021-09-29', 3, 'Du lịch Phú Quốc - Thiên đường nghĩ dưỡng 3 ngày 2 đêm dịp lễ 30/4 từ Sài Gòn - Phú Quốc còn được gọi là đảo Ngọc, hòn đảo lớn nhất nước ta thuộc Vịnh Thái Lan. Hằng năm, nơi đây đón hàng triệu lượt khách du lịch Phú Quốc trong và nước bởi vẻ đẹp hoang sơ, quyến rũ. Chính vì Phú Quốc đăc biệt nên mới đươc quy hoạch là đặc khu kinh tế riêng. Riêng bản thân Phú Quốc cũng có 1 sân bat quốc tế. Tuy nhiên, không phải ai cũng có kinh nghiệm đi tour du lịch Phú Quốc ăn chơi, mua sắm và những thức liên quan khác khi du lịch Phú Quốc nhé.', 'assets/img/du-lich-phu-quoc-du-lich-bien-du-lich-viet(1).jpg', 5699000, 10, 5),
(79, 'Du lịch Miền Trung - Đà Nẵng - Bà Nà - Hội An - Huế - Động Phong Nha từ Sài Gòn 2020', '2021-09-30', 4, 'Khám phá trọn vẹn vẻ đẹp tạo hóa ban tặng cho dải đất miền Trung với hành trình du lịch Đà Nẵng – Bà Nà – Hội An – Huế - Thánh Địa La Vang – Động Phong Nha 4 ngày 3 đêm. Đặc biệt, với hành trình này du khách còn được hưởng những ưu đã hấp dẫn cùng Du Lịch Việt, chắc chắn sẽ mang đến cho du khách một chuyến đi trọn vẹn nhất!', 'assets/img/tour-dong-phong-nha-gia-re-2019.jpg', 4999000, 10, 8),
(80, 'Du lịch Miền Trung - Đà Nẵng - Hội An - Huế - Thánh Địa La Vang - Động Thiên Đường từ Sài Gòn 2020', '2021-10-01', 4, 'Dải đất miền Trung được thiên nhiên ưu ái ban tặng cho những danh thắng đẹp trên cả tuyệt vời. Du lịch miền Trung, bạn sẽ bước chân qua những cung đường đèo uốn lượn đầy mộng mơ, ngắm nhìn những ngọn núi hùng vĩ, những bãi biển đẹp như tranh vẽ… Không chỉ có thế, đi tour miền Trung trong dịp đầu năm, bạn còn có cơ hội đến các điểm du lịch tâm linh, chắc chắn sẽ mang đến cho bạn những cảm giác mới mẻ khó quên,... Bởi gần đây, Tour lễ chùa đầu năm đã trở thành nét văn hóa đặc trưng không thể thiếu mỗi dịpTết đến Xuân về của từng du khách nói riêng và nhiều người dân Việt Nam nói chung. Và dịp nghỉ Tết Âm lịch cũng là cơ hội cho nhiều gia đình đi du lịch kết hợp hành hương, chiêm bái trở nên rất phổ biến hiện nay.', 'assets/img/ba-na-hill-du-lich-mien-trung-gia-kiet-kiem.jpg', 5399000, 10, 6),
(81, 'Du lịch miền Trung - Đà Nẵng - Bà Nà - Hội An - Huế - Hồ Truồi - Bạch Mã từ Sài Gòn 2020', '2021-10-02', 4, 'Du lịch miền Trung - Đà Nẵng - Bà Nà - Hội An - Huế - Hồ Truồi - Bạch Mã từ Sài Gòn 2020 - Du lịch miền Trung luôn hấp dẫn du khách với những món ăn ngon, bãi biển xinh đẹp và những hòn đảo thơ mộng mà ít nơi nào có được. Nhưng bên cạnh đó khi tới vùng đất này bạn còn có thể khám phá những địa danh, di tích lịch sử đã tồn tại từ hàng ngàn năm nay. Hãy cùng Du Lịch Việt khám phá những địa điểm du lịch thú vị nhé.', 'assets/img/pho-co-hoi-an-du-lich-hoi-an-2019.jpg', 4799000, 10, 7),
(82, 'Du lịch Miền Tây - Rạch Giá - Quần Đảo Nam Du - Hải Đăng Nam Du 3N3Đ từ Sài Gòn 2020', '2021-10-03', 3, 'Du lịch Rạch Giá - Quần Đảo Nam Du - Hòn Lớn - Dốc Ông Tình - Hải Đăng Nam Du - Đảo Nam Du thuộc tỉnh Kiên Giang với hơn 21 đảo hòn lớn nhỏ, cuốn hút với những bờ biển đẹp và thiên nhiên còn nhiều hoang sơ. Chương trình tham quan Đảo Nam Du 3 ngày 3 đêm lần này sẽ đưa du khách ghé thăm những địa điểm tuyệt đẹp nhất của đảo Nam Du, lặn ngắm san hô, khám phá thế giới thủy cung huyền ảo, tham quan Dốc Ông Tình, chinh phục hải đăng Nam Du - nơi cao nhất của đảo chứa đựng nhiều câu chuyện thú vị... ', 'assets/img/hon-son-tour-du-lich-mien-tay-gia-re.jpg', 2799000, 10, 5),
(83, 'Du lịch Miền Bắc - Hà Nội - Yên Tử - Hạ Long - Tràng An - Sapa - Fansipan từ Sài Gòn 2020', '2021-10-04', 6, 'Miền Bắc là nơi khởi nguồn văn hóa ngàn năm văn hiến của dân tộc Việt Nam. Du lịch miền Bắc du khách sẽ được khám phá những thắng cảnh thiên nhiên đẹp mê hồn cùng nhiều công trình kiến trúc ấn tượng được tạo nên bởi bàn tay khéo léo của con người. Điểm du lịch Tràng An là nơi du khách sẽ được khám phá một trong những địa điểm du lịch đẹp nhất Ninh Bình. Tạo hóa đã vô cùng ưu ái ban tặng cho nơi đây một cảnh quan thiên nhiên tuyệt đẹp với các dãy núi uốn lượn bao quanh các dòng Suối nước tự nhiên, tạo nên vô vàn các hang động kỳ ảo, huyền bí.... Cùng Du Lịch Việt khám phá những địa điểm du lịch miền bắc hấp dẫn nhất như Hà Nội - Yên Tử - Hạ Long - Chùa Bái Đính - Tràng An - Sapa - Bản Cát Cát - Đỉnh Fansipan,... để bắt đầu lên kế hoạch cho chuyến du lịch ngay nhé!', 'assets/img/sunworld-ha-long-park-du-lich-ha-long-gia-re.jpg.jpg', 8199000, 10, 6),
(84, 'Du lịch Miền Trung - Nha Trang - Đà Lạt Mùa Thu từ Sài Gòn\n', '2021-10-05', 5, 'Hành trình đưa du khách đến du lịch Nha Trang thành phố biển dịu dàng, hiền hòa như đúng cái tên gọi của miền đất ấy. Ai đã từng ít nhất 1 lần đặt chân tới đây đều cảm nhận sự bình yên của thành phố biển xinh đẹp thuộc vùng nam Trung Bộ này của Việt Nam. Xuôi về miền cao du khách đến với thành phố du lịch Đà lạt mộng mơ, du khách đắm mình trong cái lạnh vùng cao, hòa mình vào thiên nhiên với rất nhiều sông, hồ, thác và những rừng thông bạt ngàn. Còn gì thú vị hơn khi khi thả bộ quanh Hồ Xuân Hương hít thở khí trời tươi mát, hay Hồ Tuyền Lâm với những làn sương buổi sớm thả mình chạm khẽ mặt hồ. Với sự cổ kính của Ga Đà Lạt, hay đỉnh LangBiang với chuyện tình cảm động nhưng đầy nước mắt chính là những điểm đến lý tưởng.\r\n', 'assets/img/fairytale-land.jpg', 4099000, 10, 8),
(85, 'Du lịch Quy Nhơn - Bình Định từ Sài Gòn 2020', '2021-10-06', 3, 'Du lịch Quy Nhơn - Bình Định 3 ngày 2 đêm khởi hành từ Sài Gòn 2020 - Du khách đến thiên đường nghỉ dưỡng phố biển Quy Nhơn (Bình Định) sẽ có cơ hội khám phá ghềnh đá ven biển với muôn hình kỳ thú. Với hành trình này, Quý khách sẽ có cả một danh sách dài các thiên đường đảo và biển chờ bạn khám phá tại tour Quy Nhơn giá rẻ…', 'assets/img/Quy-Nhon-du-lich-viet.jpg', 3999000, 10, 7),
(86, 'Du lịch Tết Nguyên Đán Quy Nhơn - Phú Yên - Eo Gió - Kỳ Co từ Sài Gòn 2021', '2021-10-07', 4, 'Du lịch Tết Nguyên Đán Quy Nhơn - Phú Yên - Eo Gió - Kỳ Co từ Sài Gòn 2021- Du lịch Phú Yên với một vẻ đẹp mặn mà tựa như một cô thông nữ mộc mạc đậm chất làng quê Việt Nam sẽ thu hút bất kỳ ai du lịch Phú Yên. Vẻ đẹp của Phú Yên đã từng được nhắc đến trong nhiều câu ca bài hát, trong những bộ phim... với những cảnh quan núi xanh, nước biếc, biển sông hồ. Hôm nay chúng ta hãy cùng nhau khám phá chuyến du lịch Phú Yên này nhé. Bên cạnh đó đến với Du lịch Quy Nhơn cũng không kém phần đẹp như mơ, Quy Nhơn là thành phố biển nằm ở khu vực duyên hải Nam Trung Bộ, cách thành phố Nha Trang khoảng 213 km về phía Bắc. Nằm trên dải đất hình cánh cung, không khí mát mẻ, Quy Nhơn ôm trọn vẻ đẹp của núi biển chan hòa. Bên cạnh những thắng cảnh tuyệt đẹp, Quy Nhơn còn nổi tiếng với nhiều địa điểm tham quan du lịch hấp dẫn, đặc sản địa phương phong phú. Nào hãy cùng Du Lịch Việt chúng tôi  khám phá thành phố biển Quy Nhơn với những điểm thú vị không nơi nào có được.', 'assets/img/du-lich-phu-yen-trai-nghiem-eo-gio-du-lich-viet.jpg', 7999000, 10, 6),
(87, 'Tour du lịch Côn Đảo từ Sài Gòn 2020 giá tốt', '2021-10-07', 2, 'Cỏ ống: Địa danh này mang tên một loại cỏ hình ống, chiều cao từ nửa thước trở lên. \r\nMũi Lò Vôi : Nơi người Pháp xây nên nhằm sản xuất vôi bằng san hô và kết hợp một số phụ gia tạo thành chất kết dính thay cho xi măng.\r\nNghĩa Địa Hàng Keo: Là nghĩa địa đầu tiên tại Côn Đảo, nơi thực dân Pháp chôn cất tù nhân tử nạn trước khi có nghĩa trang Hàng Dương.\r\nDinh Chúa Đảo: Nơi lưu giữ các tư liệu và hình ảnh của Côn Đảo từ thời Pháp thuộc đến những năm 2000.\r\nPhú Hải: Trại giam đầu tiên được xây dựng tại Côn Đảo, nơi có truyền thuyết về hầm Xay Lúa.\r\nChuồng Cọp Pháp - Mỹ: Hệ thống biệt giam đặc biệt tại Côn Đảo.\r\nChuồng Bò: Nơi tra tấn man rợ của nhà cầm quyền.\r\nCầu tàu 914: Nơi đầu tiên được xây dựng trên đất Côn Đảo, với con số ước tính số tù nhân bị thiệt mạng là 914 người.', 'assets/img/du-lich-con-dao-gia-re.jpg', 6699000, 10, 7),
(88, 'Du lịch Huế - Lào Cai từ Sài Gòn 2020', '2021-10-08', 5, 'Du Lịch Lào - Đông Bắc Thái- Hành trình Khám phá “Vương Quốc Triệu Voi” 5 ngày 4 đêm là dịp để Quý du khách có thể khám phá vẻ đẹp của đất nước Triệu Voi xinh đẹp, thưởng ngoạn thêm những nét độc đáo hấp dẫn của vùng Đông Bắc Thái Lan... Tất cả sẽ mang đến cho quý khách những ngày nghỉ tuyệt vời nhất cùng nhiều trải nghiệm khó quên tại những vùng đất mới.', 'assets/img/dai-noi-tour-hue-gia-tiet-kiem.jpg', 10499000, 10, 6),
(89, 'Du lịch Nha Trang Tết Nguyên đán 2020 - Tour Đảo Bình Ba 2 Ngày 2 Đêm từ Sài Gòn', '2021-10-09', 2, 'Du lịch Nha Trang Tết Nguyên Đán 2020 đi Đảo Bình Ba - Còn gì tuyệt vời hơn, khi đón những ngày đầu năm mới tại hòn đảo hoang sơ Đảo Bình Ba - Một bức tranh thiên nhiên tuyệt đẹp được vẽ nên bởi biển trời xanh như ngọc, quanh năm nắng trải dài trên những triền cát vàng lấp lánh! \r\n', 'assets/img/resort-ngoc-suong-tour-ninh-chu-gia-re.jpg', 2899000, 10, 8),
(90, 'Tour du lịch Nha Trang mùa Thu 4 ngày 3 đêm từ Sài Gòn', '2021-10-10', 4, 'Tour du lịch mùa thu Nha Trang đưa đu khách đến Nha Trang vào thu mơ mộng và nhẹ nhàng với cái nắng vàng hoang hoải. Nắng thật nhẹ vờn trên sóng biển, nắng rót mật ngọt ngào và vàng óng trên những triền cát xa, nắng ru sóng vỗ nhẹ hơn và làm cho những làn gió mang hơi thở mênh mang của biển cả.\r\n\r\nNha Trang mùa thu đẹp tinh khôi đến lạ lùng, nắng thu như làm biển trải càng rộng và mênh mông hơn gấp bội. Nắng như dát vàng trên những triền cát trắng, níu gót mọi bước chân qua... Sở hữu cho mình những bãi cát trắng mịn đến tuyệt vời, nước biển thì có màu xanh ngắt mát mắt trong veo và núi non xung quanh tạo nên khung cảnh hoang sơ kỳ bí hấp dẫn Bãi Dài chính là điểm dừng chân mà bất kỳ ai cũng không thể nào bỏ qua cho hành trìnhdu lịch Nha Trang mùa thu. Dưới làn nắng vàng nhẹ nhàng của mùa thu bạn có thể nằm phơi mình thảnh thơi trên triền cát, cho tầm hồn lãng du theo làn gió nhẹ rồi ru mình trong giấc ngủ bình yên không mộng mị....', 'assets/img/4-dao-tour-du-lich-nha-trang-gia-tiet-kiem.jpg', 3299000, 10, 6),
(92, 'Du lịch Miền Trung - Nha Trang - Đà Lạt mùa Thu 5 ngày khởi hành từ Sài Gòn', '2021-10-12', 5, 'Hành trình đưa du khách đến du lịch Nha Trang thành phố biển dịu dàng, hiền hòa như đúng cái tên gọi của miền đất ấy. Ai đã từng ít nhất 1 lần đặt chân tới đây đều cảm nhận sự bình yên của thành phố biển xinh đẹp thuộc vùng nam Trung Bộ này của Việt Nam. Xuôi về miền cao du khách đến với thành phố du lịch Đà lạt mộng mơ, du khách đắm mình trong cái lạnh vùng cao, hòa mình vào thiên nhiên với rất nhiều sông, hồ, thác và những rừng thông bạt ngàn. Còn gì thú vị hơn khi khi thả bộ quanh Hồ Xuân Hương hít thở khí trời tươi mát, hay Hồ Tuyền Lâm với những làn sương buổi sớm thả mình chạm khẽ mặt hồ. Với sự cổ kính của Ga Đà Lạt, hay đỉnh LangBiang với chuyện tình cảm động nhưng đầy nước mắt chính là những điểm đến lý tưởng.', 'assets/img/4-dao-tour-du-lich-nha-trang-gia-re.jpg', 4099000, 10, 5),
(94, 'Du lịch Đà Lạt - Hoa Sơn Điền Trang - Trang trại rau và hoa từ Sài Gòn', '2021-10-13', 4, 'Du Lịch Đà Lạt 4 ngày 3 đêm với những điểm đến hấp dẫn, đặc biệt, hành trình đưa du khách đến check in tại F Cánh Đồng Hoa, một quán cà phê được giới trẻ săn lùng. Sở hữu kiến trúc trên cao, không gian thoáng mát với những cánh đồng hoa nữ hoàng xanh (gần giống oải hương), hoa cánh bướm… Hoặc trực tiếp xuống cánh đồng hoa, săn cho mình những bức hình cực chất khi đi tour du lịch Đà Lạt ,...', 'assets/img/da-lat-hoa-du-lich-viet.jpg', 2699000, 10, 8),
(95, 'Du lịch Phú Quốc - Trải nghiệm Cano - du ngoạn 4 đảo từ Sài Gòn 2020', '2021-10-14', 3, 'Du lịch Phú Quốc hành trình đến với đảo ngọc xinh đẹp, nơi có biển xanh, nắng vàng và hoàng hôn trên biển đẹp nhất nhì Việt Nam. Phú Quốc luôn là hòn đảo làm xiêu lòng biết bao du khách lần đầu đặt chân đến. Nằm tách biệt bên ngoài khơi xa, Phú Quốc ôm trọn vẻ đẹp của thiên nhiên với nhiều hòn đảo xinh đẹp như Hòn Thơm, Hòn Móng Tay, Hòn Gầm Ghì, Hòn Mây Rút...Một tour du lịch Phú Quốc khám phá 4 đảo và giải trí tại Vinpearl Land Phú Quốc cùng Du Lịch Việt sẽ là hành trình khám phá thiên đường biển đảo tuyệt vời dành cho bạn.', 'assets/img/du-lich-phu-quoc-hon-may-rut.jpg', 3099000, 10, 7),
(96, 'Tour du lịch Nha Trang mùa Thu 4 ngày 3 đêm từ Sài Gòn', '2021-10-16', 4, 'Tour du lịch mùa thu Nha Trang đưa đu khách đến Nha Trang vào thu mơ mộng và nhẹ nhàng với cái nắng vàng hoang hoải. Nắng thật nhẹ vờn trên sóng biển, nắng rót mật ngọt ngào và vàng óng trên những triền cát xa, nắng ru sóng vỗ nhẹ hơn và làm cho những làn gió mang hơi thở mênh mang của biển cả.\r\n\r\nNha Trang mùa thu đẹp tinh khôi đến lạ lùng, nắng thu như làm biển trải càng rộng và mênh mông hơn gấp bội. Nắng như dát vàng trên những triền cát trắng, níu gót mọi bước chân qua... Sở hữu cho mình những bãi cát trắng mịn đến tuyệt vời, nước biển thì có màu xanh ngắt mát mắt trong veo và núi non xung quanh tạo nên khung cảnh hoang sơ kỳ bí hấp dẫn Bãi Dài chính là điểm dừng chân mà bất kỳ ai cũng không thể nào bỏ qua cho hành trìnhdu lịch Nha Trang mùa thu. Dưới làn nắng vàng nhẹ nhàng của mùa thu bạn có thể nằm phơi mình thảnh thơi trên triền cát, cho tầm hồn lãng du theo làn gió nhẹ rồi ru mình trong giấc ngủ bình yên không mộng mị....', 'assets/img/4-dao-tour-du-lich-nha-trang.jpg', 3299000, 10, 5),
(97, 'Du lịch Miền Trung - Đà Nẵng - Quảng Trị - Quảng Bình dịp Hè 4 ngày xuất phát từ Sài Gòn', '2021-10-17', 4, 'Với tour Quảng Trị Hè giá rẻ 2019 đưa du khách đến với dải đất miền Trung nơi sở hữu những điểm tham quan rất đáng để, nơi có cảnh quan yên bình với những con người chân chất, mộc mạc cùng những kí ức lịch sử vẫn còn in dấu. Đến tour du lịch mùa Hè Quảng Trị, Quý khách sẽ không chỉ được nghỉ ngơi và tham quan mà hơn hết, du khách sẽ học và hiểu ra rất nhiều điều, được đong đầy trải nghiệm cuộc sống quý giá mà không phải điểm đến nào cũng mang lại cho bạn.', 'assets/img/du-lich-dong-phong-nha-quang-binh-gia-re.jpg', 5599000, 10, 6),
(98, 'Du lịch Phú Yên - Tuy Hòa - Bãi Xếp - Vũng Rô từ Sài Gòn 2020', '2021-10-18', 3, 'Du lịch phú yên 2020 đưa du khách đến với cánh đồng “hoa vàng cỏ xanh” đầy kiêu hãnh, trải nghiệm cảm giác bồi hồi khi xe qua Đèo Cả, ngắm vịnh Vũng Rô bình yên và Tháp Nhạn cổ kính. Chụp ảnh với kỳ quan Gành Đá Dĩa và Nhà thờ Mằng Lăng là một trong những kỷ niệm tuyệt vời bạn không thể bỏ qua tại dải đất Phú Yên thân thiện và mến khách. ', 'assets/img/ganh-da-dia-phu-yen-du-lich-mien-trung-gia-re.jpg', 3899000, 10, 8),
(99, 'Du lịch Cần Thơ - Cà Mau - Sóc Trăng', '2021-10-20', 4, 'Du lịch Cần Thơ Tết Dương lịch sẽ đưa du khách tham quan và chụp ảnh lưu niệm tại Mốc tọa độ Quốc gia, Panô biểu tượng Mũi Cà Mau. Hay len lỏi trong các kênh rạch của rừng đước, tận hưởng bầu không khí trong lành của rừng U Minh Hạ với những cánh rừng tràm bạt ngàn, xanh thẳm hun hút tầm mắt... Mùa nước nổi Cần Thơ là thời gian thích hợp nhất cho du khách thực hiện đi  Xuôi thuyền khám phá chợ nổi, một nét đặc trưng của miền sông nươc, hay ngồi bên nhau nghe kể chuyện chàng \"công tử bạc liêu\" ngắm những cổ vật ngay tại ngôi nhà của vị công tử nổi tiếng...Và còn nhiều trải nghiệm thú vị đang chờ du khách trong hành trình này nhé!', 'assets/img/cho-noi-cai-rang-tour-du-lich-can-tho-mua-thu.jpg', 3499000, 10, 5),
(100, 'Du lịch Miền Bắc - Du lịch Hà Nội - Hạ Long - Sapa', '2021-10-21', 4, 'Với mong muốn dùng hành động để gửi đến du khách những lời tri ân sâu sắc nhất, công ty Du Lịch Việt kết hợp với hãng hàng không Vietnam Airlines gửi đến du khách hành trình du lịch Miền Bắc– Hà Nội – Hạ Long – Yên Tử - Sapa – Fansipan 4 ngày 5 đêm với giá ưu đãi, với hành trình này còn dành được tặng thêm 20kg hành lý ký gửi giúp du khách có một chuyến đi thoải mái và an toàn nhất', 'assets/img/du-lich-ha-noi-gia-re.jpg', 7699000, 10, 5);
